document.addEventListener('DOMContentLoaded', function () {
  // right filter sidebar
  var sidebar = document.querySelector('#sidebar');

  if (sidebar) {
    sidebar.addEventListener('click', function (event) {
      var insideSection = document.querySelector('#filters_sidebar_section');

      if (!(event.target === insideSection || insideSection.contains(event.target))) {
        sidebar.classList.toggle('sidebar_open');
      }
    });
  }

  var menuButton = document.querySelector('#utility_nav');
  var menu = document.querySelector('#tabs'); // toggle menu sidebar with the menu button

  menuButton.addEventListener('click', function (event) {
    var currentUser = document.querySelector('#current_user');
    var logout = document.querySelector('#logout');
    var forbiddenLinks = event.target === logout || logout.contains(event.target) || event.target === currentUser || currentUser.contains(event.target);

    if (!forbiddenLinks) {
      menu.classList.toggle('tabs_open');
    }
  }); // close left menu sidebar on any click outside

  document.body.addEventListener('click', function (event) {
    var forbiddenLinks = event.target === menu || menu.contains(event.target) || event.target === menuButton || menuButton.contains(event.target);

    if (menu.classList.contains('tabs_open') && !forbiddenLinks) {
      menu.classList.remove('tabs_open');
    }
  }); // toggle of nested menu items

  var nestedMenuItems = document.querySelectorAll('#tabs .has_nested');
  nestedMenuItems.forEach(function (nestedMenuItem) {
    nestedMenuItem.addEventListener('click', function (e) {
      e.stopPropagation();
      nestedMenuItem.classList.toggle('open');
    });
  });
});
